import React from 'react';
import { ArrowLeft, Calendar, MapPin, DollarSign, ExternalLink, Download, Share2, Star, Wifi, Car, Coffee } from 'lucide-react';
import { TripPlan, TripResultProps } from '../types';

const TripResult: React.FC<TripResultProps> = ({ tripPlan, onReset }): JSX.Element => {
  const handleDownload = (): void => {
    const content = `
Ïó¨Ìñâ Í≥ÑÌöçÏÑú

Î™©Ï†ÅÏßÄ: ${tripPlan.destination}
Í∏∞Í∞Ñ: ${tripPlan.duration}
Ï¥ù ÎπÑÏö©: ${tripPlan.total_cost}

ÏùºÏ†ï:
${tripPlan.itinerary.map(day => `
${day.day}ÏùºÏ∞® (${day.date})
Ïò§Ï†Ñ: ${day.morning}
Ïò§ÌõÑ: ${day.afternoon}
Ï†ÄÎÖÅ: ${day.evening}
ÏàôÎ∞ï: ${day.accommodation}
`).join('')}

ÏàôÎ∞ï Ï†ïÎ≥¥:
${tripPlan.accommodation.map(acc => `
${acc.name} (${acc.type})
Í∞ÄÍ≤©ÎåÄ: ${acc.price_range}
ÏÑ§Î™Ö: ${acc.description}
${acc.rating ? `ÌèâÏ†ê: ${acc.rating}/5` : ''}
${acc.amenities ? `Ìé∏ÏùòÏãúÏÑ§: ${acc.amenities.join(', ')}` : ''}
ÏòàÏïΩ ÏÇ¨Ïù¥Ìä∏:
${Object.values(acc.booking_links).map(link => `- ${link.name}: ${link.url}`).join('\n')}
`).join('')}

Ïó¨Ìñâ ÌåÅ:
${tripPlan.tips.map(tip => `‚Ä¢ ${tip}`).join('\n')}
    `;
    
    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${tripPlan.destination}_Ïó¨ÌñâÍ≥ÑÌöçÏÑú.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleShare = async (): Promise<void> => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: `${tripPlan.destination} Ïó¨Ìñâ Í≥ÑÌöç`,
          text: `AIÍ∞Ä ÎßåÎì† ${tripPlan.destination} Ïó¨Ìñâ Í≥ÑÌöçÏùÑ ÌôïÏù∏Ìï¥Î≥¥ÏÑ∏Ïöî!`,
          url: window.location.href
        });
      } catch (error) {
        console.log('Í≥µÏú† Ï∑®ÏÜåÎê®');
      }
    } else {
      // Í≥µÏú† APIÍ∞Ä ÏßÄÏõêÎêòÏßÄ ÏïäÎäî Í≤ΩÏö∞ ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨
      const text = `${tripPlan.destination} Ïó¨Ìñâ Í≥ÑÌöç: ${window.location.href}`;
      navigator.clipboard.writeText(text);
      alert('ÎßÅÌÅ¨Í∞Ä ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
    }
  };

  return (
    <div className="max-w-6xl mx-auto">
      {/* Ìó§Îçî */}
      <div className="flex items-center justify-between mb-8">
        <button
          onClick={onReset}
          className="btn-secondary flex items-center space-x-2"
        >
          <ArrowLeft className="h-4 w-4" />
          <span>ÏÉàÎ°úÏö¥ Ïó¨Ìñâ Í≥ÑÌöç ÎßåÎì§Í∏∞</span>
        </button>
        
        <div className="flex space-x-3">
          <button onClick={handleDownload} className="btn-secondary flex items-center space-x-2">
            <Download className="h-4 w-4" />
            <span>Îã§Ïö¥Î°úÎìú</span>
          </button>
          <button onClick={handleShare} className="btn-secondary flex items-center space-x-2">
            <Share2 className="h-4 w-4" />
            <span>Í≥µÏú†</span>
          </button>
        </div>
      </div>

      {/* Ïó¨Ìñâ Í∞úÏöî */}
      <div className="card mb-8">
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">
            üéâ {tripPlan.destination} Ïó¨Ìñâ Í≥ÑÌöç ÏôÑÏÑ±!
          </h1>
          <p className="text-xl text-gray-600">AIÍ∞Ä ÎãπÏã†ÏùÑ ÏúÑÌï¥ ÎßûÏ∂§Ìòï Ïó¨Ìñâ Í≥ÑÌöçÏùÑ ÎßåÎì§ÏóàÏäµÎãàÎã§</p>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="text-center p-4 bg-blue-50 rounded-lg">
            <MapPin className="h-8 w-8 text-blue-600 mx-auto mb-2" />
            <h3 className="font-semibold text-gray-900">Î™©Ï†ÅÏßÄ</h3>
            <p className="text-gray-600">{tripPlan.destination}</p>
          </div>
          <div className="text-center p-4 bg-green-50 rounded-lg">
            <Calendar className="h-8 w-8 text-green-600 mx-auto mb-2" />
            <h3 className="font-semibold text-gray-900">Ïó¨Ìñâ Í∏∞Í∞Ñ</h3>
            <p className="text-gray-600">{tripPlan.duration}</p>
          </div>
          <div className="text-center p-4 bg-purple-50 rounded-lg">
            <DollarSign className="h-8 w-8 text-purple-600 mx-auto mb-2" />
            <span className="font-semibold text-gray-900">ÏòàÏÉÅ ÎπÑÏö©</span>
            <p className="text-gray-600">{tripPlan.total_cost}</p>
          </div>
        </div>
      </div>

      {/* ÏÉÅÏÑ∏ ÏùºÏ†ï */}
      <div className="card mb-8">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">üìÖ ÏÉÅÏÑ∏ ÏùºÏ†ï</h2>
        <div className="space-y-6">
          {tripPlan.itinerary.map((day, index) => (
            <div key={index} className="border-l-4 border-primary-500 pl-6 py-4">
              <div className="flex items-center mb-3">
                <span className="bg-primary-600 text-white px-3 py-1 rounded-full text-sm font-medium">
                  {day.day}ÏùºÏ∞®
                </span>
                <span className="ml-3 text-gray-500">{day.date}</span>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="bg-blue-50 p-3 rounded-lg">
                  <h4 className="font-semibold text-blue-900 mb-1">üåÖ Ïò§Ï†Ñ</h4>
                  <p className="text-blue-800">{day.morning}</p>
                </div>
                <div className="bg-yellow-50 p-3 rounded-lg">
                  <h4 className="font-semibold text-yellow-900 mb-1">‚òÄÔ∏è Ïò§ÌõÑ</h4>
                  <p className="text-yellow-800">{day.afternoon}</p>
                </div>
                <div className="bg-purple-50 p-3 rounded-lg">
                  <h4 className="font-semibold text-purple-900 mb-1">üåô Ï†ÄÎÖÅ</h4>
                  <p className="text-purple-800">{day.evening}</p>
                </div>
              </div>
              
              <div className="mt-3 p-3 bg-gray-50 rounded-lg">
                <h4 className="font-semibold text-gray-900 mb-1">üè® ÏàôÎ∞ï</h4>
                <p className="text-gray-700">{day.accommodation}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ÏàôÎ∞ï Ï†ïÎ≥¥ */}
      <div className="card mb-8">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">üè® Ï∂îÏ≤ú ÏàôÎ∞ï</h2>
        <div className="grid grid-cols-1 gap-6">
          {tripPlan.accommodation.map((acc, index) => (
            <div key={index} className="border border-gray-200 rounded-lg p-6 hover:shadow-md transition-shadow">
              <div className="flex items-start justify-between mb-4">
                <div className="flex-1">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">{acc.name}</h3>
                  <div className="flex items-center space-x-4 mb-3">
                    <span className="text-sm text-gray-500">{acc.type}</span>
                    <span className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm font-medium">
                      {acc.price_range}
                    </span>
                    {acc.rating && (
                      <div className="flex items-center space-x-1">
                        <Star className="h-4 w-4 text-yellow-500 fill-current" />
                        <span className="text-sm text-gray-700">{acc.rating}</span>
                      </div>
                    )}
                  </div>
                  
                  {/* ÏúÑÏπò Ï†ïÎ≥¥ */}
                  {acc.location && (
                    <div className="mb-3">
                      <span className="inline-flex items-center px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                        <MapPin className="h-3 w-3 mr-1" />
                        {acc.location}
                      </span>
                    </div>
                  )}
                  
                  <p className="text-gray-600 mb-4">{acc.description}</p>
                  
                  {/* Ìé∏ÏùòÏãúÏÑ§ */}
                  {acc.amenities && acc.amenities.length > 0 && (
                    <div className="mb-4">
                      <h4 className="text-sm font-medium text-gray-700 mb-2">Ìé∏ÏùòÏãúÏÑ§</h4>
                      <div className="flex flex-wrap gap-2">
                        {acc.amenities.map((amenity, idx) => (
                          <span key={idx} className="inline-flex items-center px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                            {amenity === 'Î¨¥Î£å WiFi' && <Wifi className="h-3 w-3 mr-1" />}
                            {amenity === 'Ï£ºÏ∞®' && <Car className="h-3 w-3 mr-1" />}
                            {amenity === 'Ï°∞Ïãù' && <Coffee className="h-3 w-3 mr-1" />}
                            {amenity === 'ÏàòÏòÅÏû•' && <span className="mr-1">üèä</span>}
                            {amenity === 'Ïä§Ìåå' && <span className="mr-1">üíÜ</span>}
                            {amenity === 'ÌîºÌä∏ÎãàÏä§' && <span className="mr-1">üí™</span>}
                            {amenity === 'Í≥®ÌîÑÏû•' && <span className="mr-1">‚õ≥</span>}
                            {amenity === 'Î†àÏä§ÌÜ†Îûë' && <span className="mr-1">üçΩÔ∏è</span>}
                            {amenity === 'Ï£ºÎ∞©' && <span className="mr-1">üç≥</span>}
                            {amenity === 'Î∞îÎ≤†ÌÅê' && <span className="mr-1">üî•</span>}
                            {amenity === 'ÏÑ∏ÌÉÅÍ∏∞' && <span className="mr-1">üß∫</span>}
                            {amenity === 'ÎØ∏ÏäêÎû≠ Î†àÏä§ÌÜ†Îûë' && <span className="mr-1">‚≠ê</span>}
                            {amenity}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
              
              {/* ÏòàÏïΩ ÎßÅÌÅ¨ */}
              <div className="border-t pt-4">
                <h4 className="text-sm font-medium text-gray-700 mb-3">ÏòàÏïΩ ÏÇ¨Ïù¥Ìä∏</h4>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                  {Object.entries(acc.booking_links).map(([key, link]) => (
                    <a
                      key={key}
                      href={link.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex flex-col items-center p-3 bg-gray-50 hover:bg-gray-100 rounded-lg transition-colors border border-gray-200 hover:border-gray-300"
                      title={`${link.name}ÏóêÏÑú ${acc.name} ÏòàÏïΩÌïòÍ∏∞`}
                    >
                      <span className="text-2xl mb-1">{link.icon}</span>
                      <span className="text-xs font-medium text-gray-700 text-center">{link.name}</span>
                      <ExternalLink className="h-3 w-3 text-gray-500 mt-1" />
                    </a>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Ïó¨Ìñâ ÌåÅ */}
      <div className="card">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">üí° Ïó¨Ìñâ ÌåÅ</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {tripPlan.tips.map((tip, index) => (
            <div key={index} className="flex items-start space-x-3 p-4 bg-yellow-50 rounded-lg">
              <span className="text-yellow-600 text-lg">üí°</span>
              <p className="text-gray-800">{tip}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TripResult;
