import React, { useState } from 'react';
import { Search, Calendar, MapPin, DollarSign, Heart, Users, Bed } from 'lucide-react';
import axios from 'axios';
import { TripPlan, TripPlannerProps, FormData } from '../types';

const TripPlanner: React.FC<TripPlannerProps> = ({ onTripGenerated, loading, setLoading }): JSX.Element => {
  const [formData, setFormData] = useState<FormData>({
    destination: '',
    start_date: '',
    end_date: '',
    budget: 'Î≥¥ÌÜµ',
    interests: [],
    guests: 2,
    rooms: 1
  });

  const interestOptions: string[] = [
    'ÏûêÏó∞ Í¥ÄÍ¥ë', 'Î¨∏Ìôî Ï≤¥Ìóò', 'ÎßõÏßë ÌÉêÎ∞©', 'ÏáºÌïë', 'Ïï°Ìã∞ÎπÑÌã∞', 'Ìú¥Ïñë', 'Ïó≠ÏÇ¨ ÌÉêÎ∞©'
  ];

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>): void => { //Ïù¥Î≤§Ìä∏Í∞Ä input ÎòêÎäî selectÏóêÏÑú Î∞úÏÉùÌï† Ïàò ÏûàÎã§Îäî ÌÉÄÏûÖ ÏßÄÏ†ï
    const { name, value } = e.target; //Ïù¥Î≤§Ìä∏ Í∞ùÏ≤¥ÏóêÏÑú Í∞í Í∫ºÎÇ¥Í∏∞c v
    setFormData(prev => ({
      ...prev, //Ïù¥Ï†Ñ ÏÉÅÌÉúÎ•º Í∑∏ÎåÄÎ°ú Î≥µÏÇ¨
      [name]: name === 'guests' || name === 'rooms' ? parseInt(value) : value //ÎßåÏïΩ ÏûÖÎ†•Ìïú Í∞íÏù¥ guestsÎÇò rooms Í∞ôÏùÄ Ïà´Ïûê ÌïÑÎìúÎùºÎ©¥ parseInt(value)Î°ú Ïà´ÏûêÎ°ú Î≥ÄÌôò
    }));
  };

  const handleInterestToggle = (interest: string): void => {
    setFormData(prev => ({
      ...prev,
      interests: prev.interests.includes(interest)
        ? prev.interests.filter(i => i !== interest)
        : [...prev.interests, interest]
    }));
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>): Promise<void> => {
    e.preventDefault();
    
    if (!formData.destination || !formData.start_date || !formData.end_date) {
      alert('ÌïÑÏàò Ï†ïÎ≥¥Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    
    try {
      const response = await axios.post<TripPlan>('http://localhost:8000/plan-trip', formData);
      onTripGenerated(response.data);
    } catch (error) {
      console.error('Ïó¨Ìñâ Í≥ÑÌöç ÏÉùÏÑ± Ïò§Î•ò:', error);
      alert('Ïó¨Ìñâ Í≥ÑÌöç ÏÉùÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="text-center mb-8">
        <h2 className="text-3xl font-bold text-gray-900 mb-4">
          ÎãπÏã†ÎßåÏùò ÎßûÏ∂§ Ïó¨Ìñâ Í≥ÑÌöçÏùÑ ÎßåÎì§Ïñ¥Î≥¥ÏÑ∏Ïöî
        </h2>
        <p className="text-lg text-gray-600">
          AIÍ∞Ä Ï∂îÏ≤úÌïòÎäî ÏµúÏ†ÅÏùò Ïó¨Ìñâ ÏΩîÏä§ÏôÄ ÏàôÎ∞ï Ï†ïÎ≥¥Î•º Ï†úÍ≥µÌï©ÎãàÎã§
        </p>
